        function validateForm() {
            var x = document.forms["profForm"]["email"].value;
            if (x == null || x == "") {
                alert("Email must be filled out");
                return false;
            } else if (emailpatt.test(x) == false) {
                alert("Invalid email address");
                return false;
            }
        }
